import React,{Component} from "react";
import http from "./httpService";
class AddPerson extends Component{
state={ 
    person : {id:"",name:"",age:"",city:"",gender:"",payment:""},
    cities: ["Delhi","Noida","Gurgaon","Jaipur"],
    edit:false,
};

componentDidMount(){
    this.fetchData();
}
async componentDidUpdate(prevProps,prevState){
    if(prevProps!==this.props){
        this.fetchData();
    }
}

async fetchData() {
    
    let {id} = this.props.match.params;
    if(id){
        let response = await http.get(`/customers/${id}`);
        let {data} = response;
        this.setState({person:data,edit:true});
   
}
else{
    let person ={id:"",name:"",age:"",city:"",gender:"",payment:""};
    this.setState({person:person, edit:false});
}

}



handleChange = (e) => {
    const {currentTarget:input} = e;
    let s1 = {...this.state};
    s1.person[input.name] = input.value;
    this.setState(s1);
}

async postData(url,obj){
    let response = await http.post(url,obj);
    console.log(response);
    this.props.history.push("/customers");
}

async putData(url,obj){
    let response = await http.put(url,obj);
    console.log(response);
    this.props.history.push("/customers");
}

handleSubmit=(e)=>{
    e.preventDefault();
    let{person,edit} = this.state;
    if(edit){
        this.putData(`/customers/${person.id}`,person);
    }
    else{
    this.postData("/customers",person);
    }

}
render(){
    let{id,name,age,city,gender,payment} = this.state.person;
    let{cities}=this.state;
return(<React.Fragment>
    <div className="container">

    <div className="form-group">
            <label>ID</label>
            <input  className="form-control" type="text" placeholder="Enter ID" name="id" value={id} id="id" onChange={this.handleChange} />

        </div>

        
        <div className="form-group">
            <label>Name</label>
            <input  className="form-control" type="text" placeholder="Enter Name" name="name" value={name} id="name" onChange={this.handleChange} />

        </div>
        <div className="form-group">
            <label>Age</label>
            <input  className="form-control" type="text" placeholder="Enter Age" name="age" value={age} id="age" onChange={this.handleChange} />

        </div><br/>
    <div className="form-group">
    <select className="form-control" name="city" value={city}  onChange={this.handleChange}>
        <option value="">
            Select city
        </option>
        {cities.map(ele=>(<option >{ele}</option>))}
        </select>

</div><br/>
<label className="form-check-label font-weight-bold">Gender</label><br/>
        <div className="form-check form-check-inline">
            <input className="form-check-input row" type="radio" name="gender" value={gender} checked={value === "Male"} onChange={this.handleChange}/>
            <label className="form-check-label">Male</label><br/>

        </div>
        <div className="form-check form-check-inline">
            <input className="form-check-input row" type="radio" name="gender" value={gender} checked={value === "Female"} onChange={this.handleChange}/>
            <label className="form-check-label">Female</label><br/>

        </div>
       




    
        <button className="btn btn-primary m-2" onClick={this.handleSubmit}>Submit</button>
        </div>
</React.Fragment>)
}
}
export default AddPerson;
