import React,{Component} from "react";
import http from "./httpService";
class AddProduct extends Component{
state={ 
    product : {id:"",name:"",price:""},
};

handleChange = (e) => {
    const {currentTarget:input} = e;
    let s1 = {...this.state};
    s1.product[input.name] = input.value;
    this.setState(s1);
}

async postData(url,obj){
    let response = await http.post(url,obj);
    console.log(response);
    this.props.history.push("/products");
}

handleSubmit=(e)=>{
    e.preventDefault();
    this.postData("/productApp/products",this.state.product);

}
render(){
    let{id,name,price} = this.state.product;
return(<React.Fragment>
    <div className="container">
        <div className="form-group">
            <label>Product Id</label>
            <input  className="form-control" type="text" placeholder="Enter Product Id" name="id" value={id} id="id" onChange={this.handleChange} />

        </div>
        <div className="form-group">
            <label>Product Name</label>
            <input  className="form-control" type="text" placeholder="Enter Product Name" name="name" value={name} id="name" onChange={this.handleChange} />

        </div>
        <div className="form-group">
            <label>Product Price</label>
            <input  className="form-control" type="text" placeholder="Enter Product Price" name="price" value={price} id="price" onChange={this.handleChange} />

        </div>
        <button className="btn btn-primary m-2" onClick={this.handleSubmit}>Submit</button>
        </div>
</React.Fragment>)
}
}
export default AddProduct;
