import React,{Component} from "react";
import http from "./httpService";
class AddPerson extends Component{
state={ 
    person : {name:"",age:"",city:"",company:""},
    cities: ["London","Paris","New Delhi","Bangalore"],
    companies:["Apple","Google","Facebook","Microsoft","Tesla"],
};

handleChange = (e) => {
    const {currentTarget:input} = e;
    let s1 = {...this.state};
    s1.person[input.name] = input.value;
    this.setState(s1);
}

async postData(url,obj){
    let response = await http.post(url,obj);
    console.log(response);
    this.props.history.push("/persons");
}

handleSubmit=(e)=>{
    e.preventDefault();
    this.postData("/personApp/persons",this.state.person);

}
render(){
    let{name,age,city,company} = this.state.person;
    let{cities,companies}=this.state;
return(<React.Fragment>
    <div className="container">
        
        <div className="form-group">
            <label>Name</label>
            <input  className="form-control" type="text" placeholder="Enter Name" name="name" value={name} id="name" onChange={this.handleChange} />

        </div>
        <div className="form-group">
            <label>Age</label>
            <input  className="form-control" type="text" placeholder="Enter Age" name="age" value={age} id="age" onChange={this.handleChange} />

        </div><br/>
    <div className="form-group">
    <select className="form-control" name={city} value={city} onChange={this.handleChange}>
        <option value="">
            Select city
        </option>
        {cities.map(ele=>(<option>{ele}</option>))}
        </select>

</div><br/>

<div className="form-group">
    <select className="form-control"  onChange={this.handleChange}>
        <option value="">
            Select company
        </option>
        {companies.map(ele=>(<option name={company} value={ele}>{ele}</option>))}
        </select>

</div>

    
        <button className="btn btn-primary m-2" onClick={this.handleSubmit}>Submit</button>
        </div>
</React.Fragment>)
}
}
export default AddPerson;
