import React,{Component} from "react";
import http from "./httpService.js";
import { Link } from "react-router-dom";
class Person extends Component{
    state = {person : {}};

    async componentDidMount() {
        let {id} = this.props.match.params;
        let response = await http.get(`/personApp/persons/${id}`);
    
        let {data} = response;
        this.setState({person:data});
    }
render(){
    const {person} = this.state;

return(<div className="container">
    <h4>Details of the Employee : {person.name}</h4>
    Id : {person.id}
    <br/>
    Name : {person.name}
    <br/>
    Age : {person.age}
    <br/>
    City : {person.city}
    <br/>
    Company : {person.company}
    <br/>
    <div className="row">
        <div className="col-1">
        <button className="btn btn-warning m-2 "><Link className="text-dark" to={`/persons/${person.id}/edit`}>Edit</Link></button>
        </div>
        <div className="col-2">
        <button className="btn btn-danger m-2"><Link className="text-dark" to={`/persons/${person.id}/delete`}>Delete</Link></button>
        </div>
    </div>
    <br/>
    

</div>)
}
}
export default Person;
