import React,{Component} from "react";
import Navbar from "./navbar";
// import Person from "./person";
import Persons from "./persons";
import AddPerson from "./addPerson";
import DeletePerson from "./deletePerson";
import { Route,Switch,Redirect } from "react-router-dom";
class MainComponent extends Component{

render(){
    

return(<div className="container">
    <Navbar/>

    <Switch>
                <Route path="/customers/add" component={AddPerson}/>
                <Route path="/customers/:id/delete" component={DeletePerson} />
                <Route path="/customers/:id/edit" component={AddPerson} />

                {/* <Route path="/persons/:id" component={Person}/> */}
                <Route path="/customers" component={Persons}/>
                {/* <Route path="/persons/:id/delete" component={DeletePerson} /> */}
                <Redirect from="/" to="/customers"/>



    </Switch> 
</div>)
}
}
export default MainComponent;
