import React,{Component} from "react";
import { Link } from "react-router-dom";
import queryString from "query-string";
import http from "./httpService.js";
import LeftPanelOptionsCB from "./leftPanelOptionsCB.jsx";
class Persons extends Component{
    state={data:{}}

    async fetchData() {
        let queryParams = queryString.parse(this.props.location.search);
    let {page = "1"} = queryParams;
    let response = await http.get(`/personApp/persons?page=${page}`);
    console.log(response);
    let {data} = response;
    this.setState({data:data});

    }
     componentDidMount() {
        this.fetchData();
    
    
}
componentDidUpdate(prevProps,prevState){
    if(prevProps !== this.props) this.fetchData();

}

handlePage = (incr) => {
    let queryParams = queryString.parse(this.props.location.search);
    let {page = "1"} = queryParams;
    let newPage = +page + incr;
    queryParams.page = newPage;
    this.callURL("/persons",queryParams);

}

callURL = (url,options) => {
    let searchString = this.makeSearchString(options);
    this.props.history.push({
        pathname: url,
        search:searchString,
    })

}

makeSearchString = (options) => {
    let {page} = options;
    let searchStr = "";
    searchStr = this.addToQueryString(searchStr,"page",page);
    return searchStr;
}

addToQueryString = (str,paramName,paramValue) =>
    paramValue ? str ? `${str}&${paramName}=${paramValue}` :`${paramName}=${paramValue}`: str;
render(){
    const {startIndex,endIndex,numOfItems,persons=[]}=this.state.data;
return(<React.Fragment>
    <div className="container">
        <h4>List of Persons</h4>
        <h6>Showing {startIndex} to {endIndex} of {numOfItems}</h6>
        <div className="row">
            <div className="col-3">

            </div>
            <div className="col-9">

            
        {persons.map((pr) => (
            <div className="row" key={pr.id}>
                {/* <div className="col-2 border">
                    <Link to = {`/products/${pr.id}`}>{pr.id}</Link>
                </div> */}
                <div className="col-2 border"><Link to={`/persons/${pr.id}`}>{pr.id}</Link></div>
                <div className="col-2 border">{pr.name}</div>
                <div className="col-2 border">{pr.age}</div>
                <div className="col-2 border">{pr.city}</div>
                <div className="col-2 border">{pr.company}</div>
                </div>

        ))}
        <div className="row">
            <div className="col-2">{startIndex>1 ?<button className="btn btn-primary btn-sm" onClick={()=>this.handlePage(-1)}>Prev</button>:""}</div>
            <div className="col-8">

            </div>
            <div className="col-2">
            {endIndex<numOfItems ?<button className="btn btn-primary btn-sm" onClick={()=>this.handlePage(1)}>Next</button>:""}
            </div>
            </div>

        </div>
        </div>
    </div>
</React.Fragment>)
}
}
export default Persons;
