import React,{Component} from "react";
class LeftPanelOptionsCB extends Component{

handleChange = (e) => {
    let{currentTarget:input} =e;
    let options = {...this.props.options};
    // if(input.name === "city" || input.name === "company") options[input.name] = this.updateCBs(
    //     options[input.name],
    //     input.checked,
    //     input.value
    // );
     options[input.name] = input.value;
    
    this.props.onOptionChange(options);
};



updateCBs=(inpValue,checked,value) =>{
    

    let inpArr =inpValue ? inpValue.split(",") : [];
    if(checked) inpArr.push(value);
    else{
        let index = inpArr.findIndex(ele=>ele===value);
        if(index >= 0) inpArr.splice(index,1);
    }

    return inpArr.join(',');
}

makeDropdown = (arr,value,name,label) => (
    <div className="form-group">
    <select className="form-control" name={name} value={value}  onChange={this.handleChange}>
        <option value="">
            {label}
        </option>
        {arr.map(ele=>(<option >{ele}</option>))}
        </select>

</div>
);

makeCheckboxes=(arr,values,name,label)=>{
    return(<React.Fragment>
        <label className="form-check-label font-weight-bold">{label}</label>
        {arr.map((opt)=>(<div className="form-check" key={opt}>
            <input className="form-check-input" type="checkbox" name={name} value={opt} checked={values.find((sel) => sel === opt) || false} onChange={this.handleChange}/>
            <label className="form-check-label">{opt}</label>
        </div>))}

    </React.Fragment>
    );
};

makeRadio = (arr,value,name,label) => (
    <React.Fragment>

<label className="form-check-label font-weight-bold">{label}</label><br/>
        {arr.map(ele=>(<div className="form-check form-check-inline">
            <input className="form-check-input row" type="radio" name={name} value={ele} checked={value === ele} onChange={this.handleChange}/>
            <label className="form-check-label">{ele}</label><br/>

        </div>))}
        </React.Fragment>
)



render(){
    let{id="", name="", city="", age="", gender="", payment=""}= this.props.options;
    let {cities} = this.props;
    
return(<React.Fragment>
    <div className="row border bg-light">
        
        
        
        <div className="col-12">
            {this.makeRadio(cities,city,"city","Select City")}
        </div>
        <div className="col-12">
            {this.makeRadio(genders,gender,"gender","Select City")}
        </div>
        <div className="col-12">
            {this.makeRadio(payments,payment,"payment","Select City")}
        </div>
        

    </div>
</React.Fragment>)
}
}
export default LeftPanelOptionsCB;
